    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        int hargaObatBatuk = 0;
        int hargaObatSakitKepala = 0;
        int hargaObatDemam = 0;
        int hargaObatSakitGigi = 0;
        
        
        if (obhCombi.isSelected()) {
            hargaObatBatuk += 15000 ;
        } if (siladex.isSelected()) {
            hargaObatBatuk += 17000;
        } if (woods.isSelected()) {
            hargaObatBatuk += 18000;
        } 
        
        
        
        if (bodrex.isSelected()) {
            hargaObatSakitKepala += 10000;
        } if (panadol.isSelected()) {
            hargaObatSakitKepala += 8000;
        } if (oskadon.isSelected()) {
            hargaObatSakitKepala += 3000;
        } 
        
        
        if (sanmol.isSelected()) {
            hargaObatDemam += 19000;
        } if (paracetamol.isSelected()) {
            hargaObatDemam += 15000;
        } if (biogesic.isSelected()) {
            hargaObatDemam += 3000;
        } 
        
        
        if (cataflam.isSelected()) {
            hargaObatSakitGigi += 8000;
        } if (mefinal.isSelected()) {
            hargaObatSakitGigi += 10000;
        } if (ponstan.isSelected()) {
            hargaObatSakitGigi += 12000;
        } 

        int totalsemua =(hargaObatBatuk + hargaObatSakitKepala + hargaObatDemam + hargaObatSakitGigi);
        

        TFtotal.setText(Integer.toString(totalsemua)); 
    }//GEN-LAST:event_jButton1ActionPerformed

